import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar.jsx";
import TopNavbar from "./components/TopNavbar.jsx";
import Home from "./pages/Home.jsx";
import Login from "./pages/Login.jsx";
import Register from "./pages/Register.jsx";
import Dashboard from "./pages/Dashboard.jsx";
import HRDashboard from "./pages/HRDashboard.jsx";
import Skills from "./pages/Skills.jsx";
import Jobs from "./pages/Jobs.jsx";
import Learning from "./pages/Learning.jsx";
import Interview from "./pages/Interview.jsx";
import Profile from "./pages/Profile.jsx";

// Protected Route Component
const ProtectedRoute = ({ children, allowedRoles }) => {
  const user = JSON.parse(localStorage.getItem('user') || 'null');
  const token = localStorage.getItem('token');

  if (!token || !user) {
    return <Navigate to="/login" replace />;
  }

  if (allowedRoles && !allowedRoles.includes(user.role)) {
    // Redirect to appropriate dashboard based on role
    const redirectPath = user.role === 'hr' ? '/hr/dashboard' : '/employee/dashboard';
    return <Navigate to={redirectPath} replace />;
  }

  return children;
};

export default function App() {
  return (
    <Router>
      <Routes>
        {/* Public Landing Page */}
        <Route
          path="/"
          element={
            <>
              <Navbar />
              <Home />
            </>
          }
        />

        {/* Auth Pages */}
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />

        {/* ========== EMPLOYEE ROUTES ========== */}
        <Route
          path="/employee/dashboard"
          element={
            <ProtectedRoute allowedRoles={['employee', 'user']}>
              <TopNavbar />
              <Dashboard />
            </ProtectedRoute>
          }
        />
        <Route
          path="/employee/profile"
          element={
            <ProtectedRoute allowedRoles={['employee', 'user']}>
              <TopNavbar />
              <Profile />
            </ProtectedRoute>
          }
        />
        <Route
          path="/employee/jobs"
          element={
            <ProtectedRoute allowedRoles={['employee', 'user']}>
              <TopNavbar />
              <Jobs />
            </ProtectedRoute>
          }
        />
        <Route
          path="/employee/skills"
          element={
            <ProtectedRoute allowedRoles={['employee', 'user']}>
              <TopNavbar />
              <Skills />
            </ProtectedRoute>
          }
        />
        <Route
          path="/employee/learning"
          element={
            <ProtectedRoute allowedRoles={['employee', 'user']}>
              <TopNavbar />
              <Learning />
            </ProtectedRoute>
          }
        />
        <Route
          path="/employee/interview"
          element={
            <ProtectedRoute allowedRoles={['employee', 'user']}>
              <TopNavbar />
              <Interview />
            </ProtectedRoute>
          }
        />

        {/* ========== HR ROUTES ========== */}
        <Route
          path="/hr/dashboard"
          element={
            <ProtectedRoute allowedRoles={['hr']}>
              <TopNavbar />
              <HRDashboard />
            </ProtectedRoute>
          }
        />
        <Route
          path="/hr/profile"
          element={
            <ProtectedRoute allowedRoles={['hr']}>
              <TopNavbar />
              <Profile />
            </ProtectedRoute>
          }
        />
        <Route
          path="/hr/jobs"
          element={
            <ProtectedRoute allowedRoles={['hr']}>
              <TopNavbar />
              <Jobs />
            </ProtectedRoute>
          }
        />
        <Route
          path="/hr/skills"
          element={
            <ProtectedRoute allowedRoles={['hr']}>
              <TopNavbar />
              <Skills />
            </ProtectedRoute>
          }
        />

        {/* Redirect old routes to new structure */}
        <Route path="/dashboard" element={<Navigate to="/employee/dashboard" replace />} />
        <Route path="/hr-dashboard" element={<Navigate to="/hr/dashboard" replace />} />
        <Route path="/profile" element={<Navigate to="/employee/profile" replace />} />
        <Route path="/jobs" element={<Navigate to="/employee/jobs" replace />} />
        <Route path="/skills" element={<Navigate to="/employee/skills" replace />} />
        <Route path="/learning" element={<Navigate to="/employee/learning" replace />} />
        <Route path="/interview" element={<Navigate to="/employee/interview" replace />} />
      </Routes>
    </Router>
  );
}
